mapper.select:
SELECT * FROM ( SELECT ROW_NUMBER() OVER (ORDER BY TO_NUMBER(STATUS)) NUM ,MERGE_ID ,CODE ,CASE WHEN STATUS ='00' THEN 'HTTP 성공' WHEN STATUS ='02' THEN '작업 요청' WHEN STATUS ='03' THEN '약관 제작 시작' WHEN STATUS ='04' THEN '약관 제작 완료' WHEN STATUS ='90' THEN '약관 제작 실패' WHEN STATUS ='98' THEN '실패' WHEN STATUS ='99' THEN 'HTTP 실패' WHEN STATUS ='100' THEN 'HTTP 오류' ELSE STATUS END AS STATUS ,CREATOR ,TO_CHAR(CREATED,'YYYY-MM-DD') AS CREATED ,UPDATOR ,UPDATED ,PATH ,TO_CHAR(CONTRACT_DATE,'YYYY-MM-DD') AS CONTRACT_DATE ,PRODUCT_CODE ,REGISTRATION_NUM FROM MCP_TERMS_MERGE_LI WHERE 1=1
AND STATUS = #{status}
AND REGISTRATION_NUM = #{registration_num}
AND CONTRACT_DATE = #{contract_date}
AND TO_CHAR(CREATED,'YYYY-MM-DD')>= #{created_start}
AND TO_CHAR(CREATED,'YYYY-MM-DD') <= #{created_end}
AND TO_CHAR(ADD_MONTHS(SYSDATE,-3),'YYYY-MM-DD') <= CREATED AND CREATED <= TO_CHAR(ADD_MONTHS(SYSDATE,+3),'YYYY-MM-DD')
ORDER BY NUM ) WHERE NUM BETWEEN #{pageStart}+1 AND #{perPageNum}+#{pageStart}

SELECT
        COUNT(CODE)
FROM
        MCP_TERMS_MERGE_LI
WHERE 1=1

			AND STATUS = #{status}



			AND REGISTRATION_NUM = #{registration_num}


			AND CONTRACT_DATE = #{contract_date}


			AND TO_CHAR(CREATED,'YYYY-MM-DD')>= #{created_start}


			AND TO_CHAR(CREATED,'YYYY-MM-DD') <= #{created_end}


		AND TO_CHAR(ADD_MONTHS(SYSDATE,-3),'YYYY-MM-DD') <= CREATED
		AND CREATED <= TO_CHAR(ADD_MONTHS(SYSDATE,+3),'YYYY-MM-DD')
