mapper.update:
UPDATE MCP_TERMS_MERGE_GI SET STATUS = #{status}, UPDATED = SYSDATE 

	,	PATH = #{path}

WHERE MERGE_ID = #{mergeId}

UPDATE MCP_TERMS_MERGE_LI SET STATUS = #{status}, UPDATED = SYSDATE 

	,	PATH = #{path}

WHERE MERGE_ID = #{mergeId}

mapper.select:
SELECT MERGE_ID AS MERGEID ,CODE ,STATUS ,PATH FROM MCP_TERMS_MERGE_LI WHERE 1 = 1
AND MERGE_ID IN
#{item.mergeId}

SELECT MERGE_ID AS MERGEID ,ID ,CODE ,PATH FROM MCP_TERMS_MERGE_GI WHERE 1 = 1
AND MERGE_ID IN
#{item.mergeId}
